import logo from "./logo.svg";
import "./App.css";

import { useState, useEffect } from "react";
function App() {
  const [Military, setMilitary] = useState([]);
  const [Chemical, setChemical] = useState([]);
  const [oneProduct, setOneProduct] = useState([]);
  const [viewer2, setViewer2] = useState(false);
  const [checked4, setChecked4] = useState(false);
  const [index, setIndex] = useState(0);
  const [currState, setCurrState] = useState(-1);

  //VIEW CHANGES
  const addTime = () => {
    setCurrState(1);
  };
  const updateTime = () => {
    setCurrState(2);
  };
  const deleteTime = () => {
    setCurrState(3);
  };
  const readTime = () => {
    setCurrState(0);
  };
  const infoTime = () => {
    setCurrState(4);
  };
  const chemicalTime = () => {
    setCurrState(5);
  };
  const addChemicalTime = () => {
    setCurrState(6);
  };

  //REFRESH STORE LISTS
  useEffect(() => {
    getAllChemicals();
  }, [Chemical]);
  useEffect(() => {
    getAllMilitary();
  }, [Military]);
  //ROTATING ROLLBACK EFFECT
  useEffect(() => {
    getAllMilitary();
  }, [checked4]);
  //ADD STUFF TEMPLATE VARIABLE
  const [addNewProduct, setAddNewProduct] = useState({
    _id: 0,
    title: "",
    price: 0.0,
    description: "",
    category: "",
    image: "http://127.0.0.1:4000/images/",
  });
  const [addNewChemical, setAddNewChemical] = useState({
    _id: 0,
    title: "",
    price: 0.0,
    description: "",
    category: "",
    image: "http://127.0.0.1:4000/images/",
  });
  //UPDATE MILITARY TEMPLATE
  const [updateProduct, setupdateProduct] = useState({
    _id: 0,
    title: "notitle",
    price: -1,
    description: "nodesc",
    category: "nocat",
    image: "http://127.0.0.1:4000/images/",
  });
  //GENERATES PRODUCT LIST FOR MILITARY

  const showAllItems = Military.map((el) => (
    <div key={el._id} class="item">
      <img src={el.image} width={300} /> <br />
      Title: {el.title} <br />
      Category: {el.category} <br />
      Price: {el.price} <br />
      <br />
    </div>
  ));
  //GENERATED PRODUCT LIST FOR CHEMICALS
  const showAllchemicals = Chemical.map((el) => (
    <div key={el._id} class="item">
      <img src={el.image} width={300} /> <br />
      Title: {el.title} <br />
      Category: {el.category} <br />
      Price: {el.price} <br />
      <br />
    </div>
  ));
  //FETCHES MILITARY PRODUCTS FROM DATABASE
  function getAllMilitary() {
    fetch("http://localhost:4000/military")
      .then((response) => response.json())
      .then((data) => {
        console.log("Show Catalog of Products :");
        console.log(data);
        setMilitary(data);
      });
  }
  //FETCHES CHEMICALS FROM DATABASE
  function getAllChemicals() {
    fetch("http://localhost:4000/chemical")
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setChemical(data);
      });
  }
  //GET SINGULAR MILITARY FROM DATABASE
  function getOneProduct(id) {
    console.log(id);
    if (id >= 1 && id <= 20) {
      fetch("http://localhost:4000/" + id + "/military")
        .then((response) => response.json())
        .then((data) => {
          console.log("Show one product :", id);
          console.log(data);
          const dataArr = [];
          dataArr.push(data);
          setOneProduct(dataArr);
        });
      setViewer2(!viewer2);
    } else {
      console.log("Wrong number of Product id.");
    }
  }

  //GENERATE SINGULAR ITEM FOR DISPLAT
  const showOneItem = oneProduct.map((el) => (
    <div key={el._id}>
      <img src={el.image} width={300} /> <br />
      Title: {el.title} <br />
      Category: {el.category} <br />
      Price: {el.price} <br />
    </div>
  ));
  //HANDLES CHANGE IN TYPING WJEN ADDING A NEW ITEM
  function handleChange(evt) {
    const value = evt.target.value;
    if (evt.target.name === "_id") {
      setAddNewProduct({ ...addNewProduct, _id: value });
    } else if (evt.target.name === "title") {
      setAddNewProduct({ ...addNewProduct, title: value });
    } else if (evt.target.name === "price") {
      setAddNewProduct({ ...addNewProduct, price: value });
    } else if (evt.target.name === "description") {
      setAddNewProduct({ ...addNewProduct, description: value });
    } else if (evt.target.name === "category") {
      setAddNewProduct({ ...addNewProduct, category: value });
    } else if (evt.target.name === "image") {
      const temp = value;
      setAddNewProduct({ ...addNewProduct, image: temp });
    }
  }
  //HANDLES SAME THING FOR CHEMICAL
  function handleChangeChemical(evt) {
    const value = evt.target.value;
    if (evt.target.name === "_idchemical") {
      setAddNewChemical({ ...addNewChemical, _id: value });
    } else if (evt.target.name === "titlechemical") {
      setAddNewChemical({ ...addNewChemical, title: value });
    } else if (evt.target.name === "pricechemical") {
      setAddNewChemical({ ...addNewChemical, price: value });
    } else if (evt.target.name === "descriptionchemical") {
      setAddNewChemical({ ...addNewChemical, description: value });
    } else if (evt.target.name === "categorychemical") {
      setAddNewChemical({ ...addNewChemical, category: value });
    } else if (evt.target.name === "imagechemical") {
      const temp = value;
      setAddNewChemical({ ...addNewChemical, image: temp });
    }
  }
  //HANDLES FORM CHANGE FOR UPDATING
  function handleupdateChange(evt) {
    const value = evt.target.value;
    if (evt.target.name === "_idupdate") {
      setupdateProduct({ ...updateProduct, _id: value });
    } else if (evt.target.name === "priceupdate") {
      setupdateProduct({ ...updateProduct, price: value });
    }
  }
  //HANDLES SUBMISSION OF UPDATE TO DATABASE OF MILITARY
  function handleupdateOnSubmit(e) {
    e.preventDefault();
    console.log(e.target.value);
    fetch("http://localhost:4000/Update/military", {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(updateProduct),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Post a new product completed");
        console.log(data);
        if (data) {
          //const keys = Object.keys(data);
          const value = Object.values(data);
          alert(value);
        }
      });
    getAllMilitary();
  }
  //HANDLES ADDING A NEW MILITARY ITEM SUBMIT BUTTON TO DATABASE
  function handleOnSubmit(e) {
    e.preventDefault();
    console.log(e.target.value);
    fetch("http://localhost:4000/insert/military", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(addNewProduct),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Post a new military completed");
        console.log(data);
        if (data) {
          //const keys = Object.keys(data);
          const value = Object.values(data);
          alert(value);
        }
      });
  }
  //HANDLES ADDING A NEW CHEMICAL TO DATABASE
  function handleOnSubmitChemical(e) {
    e.preventDefault();
    console.log(e.target.value);
    fetch("http://localhost:4000/insert/chemical", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(addNewChemical),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Post a new Chemical completed");
        console.log(data);
        if (data) {
          //const keys = Object.keys(data);
          const value = Object.values(data);
          alert(value);
        }
      });
  }
  //ROTATING PRODUCT FUNCTION NEXT
  function getOneByOneProductNext() {
    if (Military.length > 0) {
      if (index === Military.length - 1) setIndex(0);
      else setIndex(index + 1);
      if (Military.length > 0) setChecked4(true);
      else setChecked4(false);
    }
  }
  //ROTATING PRODCUT FUNCTION PREVIOUS
  function getOneByOneProductPrev() {
    if (Military.length > 0) {
      if (index === 0) setIndex(Military.length - 1);
      else setIndex(index - 1);
      if (Military.length > 0) setChecked4(true);
      else setChecked4(false);
    }
  }
  //HANDLES DELETING FROM DATABASE
  function deleteOneProduct(deleteid) {
    console.log("Product to delete :", deleteid);
    fetch("http://localhost:4000/delete/military", {
      method: "DELETE",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ _id: deleteid }),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Delete a military completed : ", deleteid);
        console.log(data);
        if (data) {
          //const keys = Object.keys(data);
          const value = Object.values(data);
          alert(value);
        }
      });
    setChecked4(!checked4);
  }
  //RETURN VIEWS
  //HOME
  if (currState === -1) {
    return (
      <div class="index">
        <div class="header">
          <h1>Sayas Chemical and Military Surplus Emporium</h1>
        </div>
        <div class="topnav">
          <button type="button" onClick={() => readTime()} id="checkout">
            Home
          </button>
          <button type="button" onClick={() => infoTime()} id="checkout">
            Info
          </button>
        </div>
      </div>
    );
  } else if (currState === 0) {
    return (
      <div class="index">
        <div class="header">
          <h1>Sayas Chemical and Military Surplus Emporium</h1>
        </div>
        <div class="topnav">
          <button type="button" onClick={() => readTime()} id="checkout">
            Home
          </button>
          <button type="button" onClick={() => chemicalTime()} id="checkout">
            View Chemicals
          </button>
          <button type="button" onClick={() => addChemicalTime()} id="checkout">
            Add Chemical
          </button>
          <button
            type="button"
            onClick={() => {
              addTime();
            }}
            id="checkout"
          >
            Add Product
          </button>
          <button type="button" onClick={() => updateTime()} id="checkout">
            Update Product
          </button>
          <button type="button" onClick={() => deleteTime()} id="checkout">
            Delete Product
          </button>
          <button type="button" onClick={() => infoTime()} id="checkout">
            Info
          </button>
        </div>
        {<div class="products">{showAllItems}</div>}
      </div>
    );
  }
  //ADDING A NEW CHEMICAL
  else if (currState === 6) {
    return (
      <div class="addProduct">
        <div class="header">
          <h1>Sayas Chemical and Military Surplus Emporium</h1>
        </div>
        <div class="topnav">
          <button type="button" onClick={() => readTime()} id="checkout">
            Home
          </button>
          <button type="button" onClick={() => chemicalTime()} id="checkout">
            View Chemicals
          </button>
          <button type="button" onClick={() => addChemicalTime()} id="checkout">
            Add Chemical
          </button>
          <button type="button" onClick={() => addTime()} id="checkout">
            Add Product
          </button>
          <button type="button" onClick={() => updateTime()} id="checkout">
            Update Product
          </button>
          <button type="button" onClick={() => deleteTime()} id="checkout">
            Delete Product
          </button>
          <button type="button" onClick={() => infoTime()} id="checkout">
            Info
          </button>
        </div>
        <h3>Add a new Chemical:</h3>
        <form action="">
          <label for="_id">ID? </label>
          <input
            type="number"
            placeholder="id?"
            name="_idchemical"
            value={addNewChemical._id}
            onChange={handleChangeChemical}
          />
          <br></br>
          <label for="title">Title? </label>
          <input
            type="text"
            placeholder="title?"
            name="titlechemical"
            value={addNewChemical.title}
            onChange={handleChangeChemical}
          />
          <br></br>
          <label for="price">Price? </label>
          <input
            type="number"
            placeholder="price?"
            name="pricechemical"
            value={addNewChemical.price}
            onChange={handleChangeChemical}
          />
          <br></br>
          <label for="description">Description? </label>
          <input
            type="text"
            placeholder="desc?"
            name="descriptionchemical"
            value={addNewChemical.description}
            onChange={handleChangeChemical}
          />
          <br></br>
          <label for="category">Category? </label>
          <input
            type="text"
            placeholder="category?"
            name="categorychemical"
            value={addNewChemical.category}
            onChange={handleChangeChemical}
          />
          <br></br>
          <label for="image">IMG Link? </label>
          <input
            type="text"
            placeholder="image?"
            name="imagechemical"
            value={addNewChemical.image}
            onChange={handleChangeChemical}
          />
          <br></br>
          <button type="submit" onClick={handleOnSubmitChemical}>
            Submit
          </button>
        </form>
      </div>
    );
  }
  //LOOK AT THE CHEMICALS VIEW
  else if (currState === 5) {
    return (
      <div class="index">
        <div class="header">
          <h1>Sayas Chemical and Military Surplus Emporium</h1>
        </div>
        <div class="topnav">
          <button type="button" onClick={() => readTime()} id="checkout">
            Home
          </button>
          <button type="button" onClick={() => chemicalTime()} id="checkout">
            View Chemicals
          </button>
          <button type="button" onClick={() => addChemicalTime()} id="checkout">
            Add Chemical
          </button>
          <button
            type="button"
            onClick={() => {
              addTime();
            }}
            id="checkout"
          >
            Add Product
          </button>
          <button type="button" onClick={() => updateTime()} id="checkout">
            Update Product
          </button>
          <button type="button" onClick={() => deleteTime()} id="checkout">
            Delete Product
          </button>
          <button type="button" onClick={() => infoTime()} id="checkout">
            Info
          </button>
        </div>
        {<div class="products">{showAllchemicals}</div>}
      </div>
    );
  }
  //ADD A NEW MILITARY VIEW
  else if (currState === 7) {
    return (
      <div class="militaryHome">
        <div class="header">
          <h1>Sayas Chemical and Military Surplus Emporium</h1>
        </div>
        <div class="topnav">
          <button type="button" onClick={() => readTime()} id="checkout">
            Home
          </button>
          <button type="button" onClick={() => chemicalTime()} id="checkout">
            View Chemicals
          </button>
          <button type="button" onClick={() => addChemicalTime()} id="checkout">
            Add Chemical
          </button>
          <button type="button" onClick={() => addTime()} id="checkout">
            Add Product
          </button>
          <button type="button" onClick={() => updateTime()} id="checkout">
            Update Product
          </button>
          <button type="button" onClick={() => deleteTime()} id="checkout">
            Delete Product
          </button>
          <button type="button" onClick={() => infoTime()} id="checkout">
            Info
          </button>
        </div>
      </div>
    );
  } else if (currState === 1) {
    return (
      <div class="addProduct">
        <div class="header">
          <h1>Sayas Chemical and Military Surplus Emporium</h1>
        </div>
        <div class="topnav">
          <button type="button" onClick={() => readTime()} id="checkout">
            Home
          </button>
          <button type="button" onClick={() => chemicalTime()} id="checkout">
            View Chemicals
          </button>
          <button type="button" onClick={() => addChemicalTime()} id="checkout">
            Add Chemical
          </button>
          <button type="button" onClick={() => addTime()} id="checkout">
            Add Product
          </button>
          <button type="button" onClick={() => updateTime()} id="checkout">
            Update Product
          </button>
          <button type="button" onClick={() => deleteTime()} id="checkout">
            Delete Product
          </button>
          <button type="button" onClick={() => infoTime()} id="checkout">
            Info
          </button>
        </div>
        <h3>Add a new Product:</h3>
        <form action="">
          <label for="_id">ID? </label>
          <input
            type="number"
            placeholder="id?"
            name="_id"
            value={addNewProduct._id}
            onChange={handleChange}
          />
          <br></br>
          <label for="title">Title? </label>
          <input
            type="text"
            placeholder="title?"
            name="title"
            value={addNewProduct.title}
            onChange={handleChange}
          />
          <br></br>
          <label for="price">Price? </label>
          <input
            type="number"
            placeholder="price?"
            name="price"
            value={addNewProduct.price}
            onChange={handleChange}
          />
          <br></br>
          <label for="description">Description? </label>
          <input
            type="text"
            placeholder="desc?"
            name="description"
            value={addNewProduct.description}
            onChange={handleChange}
          />
          <br></br>
          <label for="category">Category? </label>
          <input
            type="text"
            placeholder="category?"
            name="category"
            value={addNewProduct.category}
            onChange={handleChange}
          />
          <br></br>
          <label for="image">IMG Link? </label>
          <input
            type="text"
            placeholder="image?"
            name="image"
            value={addNewProduct.image}
            onChange={handleChange}
          />
          <br></br>
          <button type="submit" onClick={handleOnSubmit}>
            Submit
          </button>
        </form>
      </div>
    );
  }
  //DELETE VIEW
  else if (currState === 3) {
    return (
      <div class="delete">
        <div class="header">
          <h1>Sayas Chemical and Military Surplus Emporium</h1>
        </div>
        <div class="topnav">
          <button type="button" onClick={() => readTime()} id="checkout">
            Home
          </button>
          <button type="button" onClick={() => chemicalTime()} id="checkout">
            View Chemicals
          </button>
          <button type="button" onClick={() => addChemicalTime()} id="checkout">
            Add Chemical
          </button>
          <button type="button" onClick={() => addTime()} id="checkout">
            Add Product
          </button>
          <button type="button" onClick={() => updateTime()} id="checkout">
            Update Product
          </button>
          <button type="button" onClick={() => deleteTime()} id="checkout">
            Delete Product
          </button>
          <button type="button" onClick={() => infoTime()} id="checkout">
            Info
          </button>
        </div>
        <h3>Delete one Product:</h3>
        {checked4 && (
          <div key={Military[index]._id} class="deleteProduct">
            <img src={Military[index].image} width={300} /> <br />
            Id: {Military[index]._id} <br />
            Title: {Military[index].title} <br />
            Category: {Military[index].category} <br />
            Price: {Military[index].price} <br />
          </div>
        )}
        <input
          type="checkbox"
          id="acceptdelete"
          name="acceptdelete"
          checked={checked4}
          onChange={(e) => setChecked4(!checked4)}
        />
        <button onClick={() => getOneByOneProductPrev()}>Prev</button>
        <button onClick={() => getOneByOneProductNext()}>Next</button>
        <button onClick={() => deleteOneProduct(Military[index]._id)}>
          Delete
        </button>
      </div>
    );
  }
  //UPDATE VIEW
  else if (currState === 2) {
    return (
      <div class="update">
        <div class="header">
          <h1>Sayas Chemical and Military Surplus Emporium</h1>
        </div>
        <div class="topnav">
          <button type="button" onClick={() => readTime()} id="checkout">
            Home
          </button>
          <button type="button" onClick={() => chemicalTime()} id="checkout">
            View Chemicals
          </button>
          <button type="button" onClick={() => addChemicalTime()} id="checkout">
            Add Chemical
          </button>
          <button type="button" onClick={() => addTime()} id="checkout">
            Add Product
          </button>
          <button type="button" onClick={() => updateTime()} id="checkout">
            Update Product
          </button>
          <button type="button" onClick={() => deleteTime()} id="checkout">
            Delete Product
          </button>
          <button type="button" onClick={() => infoTime()} id="checkout">
            Info
          </button>
        </div>

        <div class="updateProduct">
          <br></br>
          <div>Search for Product by Id</div>
          <input
            type="text"
            id="message"
            name="message"
            placeholder="id"
            onChange={(e) => getOneProduct(e.target.value)}
          />
          {viewer2 && <div>Product: {showOneItem}</div>}
          <h3>Update a Product by Id :</h3>
          <form action="">
            <label for="_idupdate">Current ID? </label>
            <input
              type="number"
              placeholder="id?"
              name="_idupdate"
              value={updateProduct._id}
              onChange={handleupdateChange}
            />
            <br></br>

            <br></br>
            <label for="priceupdate">New Price? </label>
            <input
              type="number"
              placeholder="price?"
              name="priceupdate"
              value={updateProduct.price}
              onChange={handleupdateChange}
            />
            <br></br>
            <button type="submit" onClick={handleupdateOnSubmit}>
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  }
  //INFO VIEW
  else if (currState == 4) {
    return (
      <div class="info">
        <div class="header">
          <h1>Sayas Chemical and Military Surplus Emporium</h1>
        </div>
        <div class="topnav">
          <button type="button" onClick={() => readTime()} id="checkout">
            Home
          </button>
          <button type="button" onClick={() => chemicalTime()} id="checkout">
            View Chemicals
          </button>
          <button type="button" onClick={() => addChemicalTime()} id="checkout">
            Add Chemical
          </button>
          <button type="button" onClick={() => addTime()} id="checkout">
            Add Product
          </button>
          <button type="button" onClick={() => updateTime()} id="checkout">
            Update Product
          </button>
          <button type="button" onClick={() => deleteTime()} id="checkout">
            Delete Product
          </button>
          <button type="button" onClick={() => infoTime()} id="checkout">
            Info
          </button>
        </div>
        <br></br>
        <strong>Intoduction:</strong> Formerly the largest Midwest distributor
        for United States Military memorabilia and vehicles, now the largest
        distributor <strong>worldwide</strong> for all military and chemical
        products. Serving the community since 1776. <br></br> <br></br>
        <strong>The Founders:</strong> Tristan Sayasit and Bilal Hodzic
        <br></br>
        <img
          src="http://127.0.0.1:4000/images/4lthefounders.jpg"
          width="300"
        ></img>
        <br></br>
        <strong>Emails:</strong> sayasit7@iastate.edu and bhodzic@iastate.edu
        <br></br>
        <strong>Course:</strong> SE/ComS319 Construction of User Interfaces
        <br></br>
        <strong>Date:</strong> Spring 2023, 5/5/2023 <br></br>
        <strong>Professor:</strong> Dr. Abraham N. Aldaco Gastelum <br></br>
        <strong>Professor Email:</strong> aaldaco@iastate.edu <br></br>
      </div>
    );
  }
} // App end
export default App;
